import React from 'react'
import { Virtuoso } from 'react-virtuoso'
import { getUser } from './FakeData'
import {
  ExampleListItem,
  ExampleAvatar,
  ExampleUserInfo,
} from './ExampleComponents'

export default () => (
  <Virtuoso
    style={{ width: '100%', height: '400px' }}
    totalCount={100}
    item={index => {
      const user = getUser(index)
      const title = `${index + 1}. ${user.name}`
      return (
        <ExampleListItem even={index % 2 === 0}>
          <ExampleAvatar
            style={{
              color: user.fgColor,
              backgroundColor: user.bgColor,
            }}
          >
            {user.initials}
          </ExampleAvatar>
          <ExampleUserInfo title={title}>
            {user.longText}
          </ExampleUserInfo>
        </ExampleListItem>
      )
    }}
    footer={() => (
      <div style={{ padding: '1rem', textAlign: 'center' }}>
        -- end reached --
      </div>
    )}
  />
)

import React, { useRef } from 'react'
import { Virtuoso } from 'react-virtuoso'
import { getUser } from './FakeData'
import { UserItem } from './ExampleComponents'

const GenerateItem = index => {
  return <UserItem user={getUser(index)} index={index} />
}

// className is string | undefined, passed from
// the root component
//
// style is CSSProperties, setting the size and the
// overflow of the element. You may have to discard
// some of those if setting up a custom scroller.
//
// scrollTo is an event emitter, which executes
// the callback when the scrollToIndex method is called
//
// reportScrollTop should be wired up to the onScroll event,
// passing the current scrollTop position
const MyScrollContainer = ({
  className,
  style,
  reportScrollTop,
  scrollTo,
  children,
}) => {
  const elRef = useRef(null)

  scrollTo(scrollTop => {
    elRef.scrollTo({ top: scrollTop })
  })

  return (
    <div
      ref={elRef}
      onScroll={e => reportScrollTop(e.target.scrollTop)}
      style={{
        ...style,
        border: '5px dashed gray',
        borderRadius: '4px',
      }}
      tabIndex={0}
      className={className}
    >
      {children}
    </div>
  )
}

export default () => (
  <Virtuoso
    ScrollContainer={MyScrollContainer}
    totalCount={1000}
    overscan={200}
    item={GenerateItem}
    style={{ height: '400px', width: '350px' }}
  />
)

import React from 'react'
import { GroupedVirtuoso } from 'react-virtuoso'

const ListContainer = ({ listRef, children, className, style }) => (
  <ul
    ref={listRef}
    className={className}
    style={{ ...style, marginBottom: 0 }}
  >
    {children}
  </ul>
)

const ItemContainer = props => <li {...props} style={{ margin: 0 }} />

const GroupContainer = ({ children, style, ...props }) => (
  <li
    {...props}
    style={{ ...style, marginBottom: 0, backgroundColor: 'white' }}
  >
    <strong>{children}</strong>
    <hr />
  </li>
)

const FooterContainer = ({ children, footerRef }) => (
  <li ref={footerRef} style={{ margin: 0 }}>
    <hr />
    {children}
  </li>
)

export default () => (
  <GroupedVirtuoso
    ListContainer={ListContainer}
    ItemContainer={ItemContainer}
    GroupContainer={GroupContainer}
    FooterContainer={FooterContainer}
    groupCounts={[10, 10, 10, 10, 10, 10]}
    item={index => <>Item {index}</>}
    group={index => <>Group {index}</>}
    footer={() => <>End</>}
    style={{ height: '400px', width: '350px' }}
  />
)

import React, {
  useState,
  useRef,
  useCallback,
  useEffect,
} from 'react'
import { Virtuoso } from 'react-virtuoso'
import { getUser } from './FakeData'
import { UserItem } from './ExampleComponents'

const GenerateItem = index => {
  return <UserItem user={getUser(index)} index={index} />
}

export default () => {
  const [total, setTotal] = useState(0)
  const items = useRef([])
  const loading = useRef(false)

  // the setTimeout below simulates a network request.
  // In the real world, you can fetch data from a service.
  // the setTotal call will trigger a refresh for the list,
  // so make sure you call it last
  const loadMore = useCallback(() => {
    if (loading.current) {
      return
    }
    loading.current = true

    setTimeout(() => {
      for (let index = total; index < total + total + 20; index++) {
        items.current = [...items.current, getUser(index)]
      }
      loading.current = false
      setTotal(items.current.length)
    }, 500)
  }, [])

  useEffect(() => {
    loadMore()
  }, [])

  return (
    <Virtuoso
      style={{ width: '350px', height: '400px' }}
      overscan={500}
      totalCount={total}
      item={GenerateItem}
      endReached={() => loadMore()}
      footer={() => {
        return (
          <div style={{ padding: '2rem', textAlign: 'center' }}>
            Loading...
          </div>
        )
      }}
    />
  )
}

