  const [isScrolling, setIsScrolling] = useState(false)

  const {
    loadMore,
    endReached,
    groupCounts,
    users,
    groups,
  } = useGroupedUsers(500)

  useEffect(loadMore, [])
  const classes = useStyles()

  return (
