import React, { useState } from 'react'
import { Virtuoso } from 'react-virtuoso'
import { getUser } from './FakeData'
import {
  ExampleListItem,
  ExampleAvatar,
  ExampleUserInfo,
  ExampleImgAvatar,
} from './ExampleComponents'

export default () => {
  const [isScrolling, setIsScrolling] = useState(false)

  return (
    <Virtuoso
      style={{
        height: '400px',
        width: '350px',
      }}
      totalCount={100}
      scrollingStateChange={scrolling => {
        setIsScrolling(scrolling)
      }}
      item={index => {
        const user = getUser(index)
        const title = `${index + 1}. ${user.name}`
        return (
          <ExampleListItem even={index % 2 === 0}>
            {isScrolling ? (
              <ExampleAvatar style={{ backgroundColor: '#ccc' }}>
                &nbsp;
              </ExampleAvatar>
            ) : (
              <ExampleImgAvatar src={user.avatar} />
            )}

            <ExampleUserInfo title={title}>
              {user.description}
            </ExampleUserInfo>
          </ExampleListItem>
        )
      }}
    />
  )
}

import React, { useState } from 'react'
import { Virtuoso } from 'react-virtuoso'
import { getUser } from './FakeData'
import { UserItem } from './ExampleComponents'

const GenerateItem = index => {
  return <UserItem user={getUser(index)} index={index} />
}

const randomHeights = Array(10)
  .fill(true)
  .map(() => Math.round(Math.random() * 60) + 10)

export default () => {
  // use the visible range to provide information
  // about the list current position.
  const [visibleRange, setVisibleRange] = useState(['-', '-'])
  return (
    <div style={{ display: 'flex' }}>
      <Virtuoso
        totalCount={10000}
        item={GenerateItem}
        scrollSeek={{
          enter: velocity => Math.abs(velocity) > 200,
          exit: velocity => {
            const shouldExit = Math.abs(velocity) < 30
            if (shouldExit) {
              setVisibleRange(['-', '-'])
            }
            return shouldExit
          },
          change: (_velocity, { startIndex, endIndex }) =>
            setVisibleRange([startIndex, endIndex]),
          // You can use index to randomize
          // and make the placeholder list more organic.
          placeholder: ({ height, index }) => (
            <div
              style={{
                height,
                backgroundColor: index % 2 ? '#fff' : '#f4f4f4',
                padding: '8px',
                boxSizing: 'border-box',
                overflow: 'hidden',
              }}
            >
              <div
                style={{
                  background: '#ccc',
                  height: randomHeights[index % 10],
                }}
              ></div>
            </div>
          ),
        }}
        style={{ height: '400px', width: '350px' }}
      />
      <div>
        Current visible range:{' '}
        <div>
          <strong>
            {visibleRange[0]} - {visibleRange[1]}
          </strong>
        </div>{' '}
      </div>
    </div>
  )
}

import React, { useState, useRef } from 'react'
import { GroupedVirtuoso } from 'react-virtuoso'
import { generateGroupedUsers } from './FakeData'
import { ExampleGroup, UserItem } from './ExampleComponents'

export default () => {
  // the generateGroupedUsers is a dummy implementation
  // that builds grouped data
  // the users variable contains 500 user records, sorted by name
  // the groups variable contains the first
  // letter groups -> ['A', 'B', 'C'], etc.
  // finally, the groupCounts specifies how many items each
  // group has -> [ 20, 30, 15, 10 ], etc.
  const { users, groups, groupCounts } = generateGroupedUsers(500)
  const virtuoso = useRef(null)
  const [groupIndices, setGroupIndices] = useState([])

  return (
    <div style={{ display: 'flex' }}>
      <div>
        <GroupedVirtuoso
          ref={virtuoso}
          groupIndices={indices => {
            if (groupIndices.length !== indices.length) {
              setGroupIndices(indices)
            }
          }}
          style={{ height: '600px', width: '300px' }}
          groupCounts={groupCounts}
          group={index => {
            return <ExampleGroup>{groups[index]}</ExampleGroup>
          }}
          item={index => {
            return <UserItem user={users[index]} index={index} />
          }}
        />
      </div>

      <ul
        style={{
          marginLeft: '1rem',
          listStyle: 'none',
          fontSize: '0.8rem',
        }}
      >
        {groupIndices.map((itemIndex, index) => (
          <li key={index}>
            <a
              href="#"
              onClick={e => {
                e.preventDefault()
                virtuoso.current.scrollToIndex(itemIndex)
              }}
            >
              {groups[index]}
            </a>
          </li>
        ))}
      </ul>
    </div>
  )
}

